import React, { Component } from 'react';
import testTodoListData from './TestTodoListData.json'
import HomeScreen from './components/home_screen/HomeScreen'
import ItemScreen from './components/item_screen/ItemScreen'
import ListScreen from './components/list_screen/ListScreen'

const AppScreen = {
  HOME_SCREEN: "HOME_SCREEN",
  LIST_SCREEN: "LIST_SCREEN",
  ITEM_SCREEN: "ITEM_SCREEN"
}

class App extends Component {
  state = {
    currentScreen: AppScreen.HOME_SCREEN,
    todoLists: testTodoListData.todoLists,
    currentList: null,
    todoItem: null,
    currentSortingCriteria: null
  }

  goHome = () => {
    this.setState({currentScreen: AppScreen.HOME_SCREEN});
    this.setState({currentList: null});
  }

  goItemScreen = () => {
    this.setState({currentScreen: AppScreen.ITEM_SCREEN});
  }

  loadList = (todoListToLoad) => {
    this.setState({currentScreen: AppScreen.LIST_SCREEN});
    this.setState({currentList: todoListToLoad});
    console.log("currentList: " + this.state.currentList);
    console.log("currentScreen: " + this.state.currentScreen);
  }

  addList = () => {
    this.setState({currentScreen: AppScreen.LIST_SCREEN});
    const newList = {
      key: this.state.todoLists.length,
      name: "Unknown",
      owner: "Unknown",
      items: []
    }
    this.setState( { todoLists: [...this.state.todoLists, newList]});
    this.setState( {currentList: newList});
    this.loadList(newList);
  }

  deleteList = () => {
    const index = this.state.todoLists.indexOf(this.state.currentList);
    this.state.todoLists.splice(index, 1);
    this.loadList();
    this.goHome();
  }

  compare = (item1, item2) => {
    if (this.state.currentSortingCriteria === "SORT_BY_TASK_DECREASING"
        || this.state.currentSortingCriteria === "SORT_BY_DUE_DATE_DECREASING"
        || this.state.currentSortingCriteria === "SORT_BY_STATUS_DECREASING") {
      let temp = item1;
      item1 = item2;
      item2 = temp;
    }
    // SORT BY ITEM DESCRIPTION
    if (this.state.currentSortingCriteria === "SORT_BY_TASK_INCREASING" 
        || this.state.currentSortingCriteria === "SORT_BY_TASK_DECREASING") {
      if (item1.description < item2.description)
                return -1;
            else if (item1.description > item2.description)
                return 1;
            else
                return 0;
        }
  }

  sortTasks = (currentSortingCriteria) => {
    this.state.currentSortingCriteria = currentSortingCriteria;
    this.state.currentList.items.sort(this.compare);
    this.loadList(this.state.currentList);
  }

  processSortItemsByTask = () => {
    if (this.state.currentSortingCriteria === "SORT_BY_TASK_INCREASING") {
      this.sortTasks("SORT_BY_TASK_DECREASING");
    }
    else {
      this.sortTasks("SORT_BY_TASK_INCREASING");
    }
  }

  render() {
    switch(this.state.currentScreen) {
      case AppScreen.HOME_SCREEN:
        return <HomeScreen 
        loadList={this.loadList.bind(this)} 
        todoLists={this.state.todoLists} 
        addList={this.addList}/>;
      case AppScreen.LIST_SCREEN:            
        return <ListScreen
          goHome={this.goHome.bind(this)}
          todoList={this.state.currentList}
          loadList={this.loadList} 
          deleteList={this.deleteList}
          addList={this.addList}
          processSortItemsByTask={this.processSortItemsByTask}
          goItemScreen={this.goItemScreen}/>;
      case AppScreen.ITEM_SCREEN:
        return <ItemScreen 
        currentScreen={this.state.currentScreen}
        currentList={this.state.currentList}
        todoItem = {this.state.todoItem}
        loadList={this.loadList}/>;
      default:
        return <div>ERROR</div>;
    }
  }
}

export default App;
