import React, { Component } from 'react'
import PropTypes from 'prop-types';

export class ItemScreen extends Component {
    addItem = () => {
        const newItem = {
            key: this.props.currentList.items.length,
            description: document.getElementsByClassName("item_description_textfield"),
            due_date: document.getElementsByClassName("item_due_date_picker"),
            completed: document.getElementsByClassName("item_completed_checkbox"),
            assigned_to: document.getElementsByClassName("item_assigned_to_textfield")
        }
        console.log(newItem);
        this.setState( {currentList: [...this.props.currentList.items, newItem]});
        console.log(this.props.currentList.items);
        this.props.loadList(this.props.currentList);
    }
    render() {
        return (
            <div>
                <h3 className="item_heading">Item</h3>
                <div className="item_form_container">
                    <div className="item_description_prompt">Description:
                    <input className="item_description_textfield"></input>
                    </div>
                    <div className="item_assigned_to_prompt">Assigned to:
                    <input className="item_assigned_to_textfield"></input>
                    </div>
                    <div className="item_due_date_prompt">Due date:
                    <input className="item_due_date_picker" type="date"/>
                    </div>
                    <div className="item_completed_prompt">Completed:
                    <input className="item_completed_checkbox" type="checkbox"/>
                    </div>
                </div>
                <button id="item_form_submit_button" onClick={this.addItem}>Submit</button>
                <button id="item_form_cancel_button" onClick={this.props.loadList}>Cancel</button>
            </div>
        )
    }
}

ItemScreen.propTypes = {
    currentScreen: PropTypes.string.isRequired,
    todoItem: PropTypes.object.isRequired
}

export default ItemScreen
