class ListItemEdit_Transaction {

    currentList = null;
    edit_new_item = null;
    currentItem = null;
    previousItem = null;
    isEditingItem = null;

    constructor(todoList, currentitem, editOrnewitem, editingItem) {
        this.currentList = todoList;
        this.previousItem = currentitem;
        this.edit_new_item = editOrnewitem;
        this.isEditingItem = editingItem;
    }

    doTransaction = () => {
        if (this.isEditingItem === false) {
            this.edit_new_item.key = this.currentList.items.length;
            this.currentList.items.push(this.edit_new_item);
        }
        else {
            this.currentItem = this.previousItem;
            // this.previousItem.description = this.currentItem.description;
            // this.previousItem.assigned_to = this.currentItem.assigned_to;
            // this.previousItem.due_date = this.currentItem.due_date;
            // this.previousItem.completed = this.currentItem.completed;

            this.currentItem.description = this.edit_new_item.description;
            this.currentItem.assigned_to = this.edit_new_item.assigned_to;
            this.currentItem.due_date = this.edit_new_item.due_date;
            this.currentItem.completed = this.edit_new_item.completed;
            console.log(this.previousItem.completed);
        }
    }

    undoTransaction = () => {
        if (this.isEditingItem === false) {
            let index = this.currentList.items.indexOf(this.edit_new_item);
            this.currentList.items.splice(index, 1);
        }
        else {
            this.currentItem.description = this.previousItem.description;
            this.currentItem.assigned_to = this.previousItem.assigned_to;
            this.currentItem.due_date = this.previousItem.due_date;
            this.currentItem.completed = this.previousItem.completed;
        }
    }
}

export default ListItemEdit_Transaction
