import React, { Component } from 'react'

export class DeleteButton extends Component {
    getItems = () => {
        return this.props.todoList.items.indexOf(this.props.listItem) !== index;
    }
    deleteItem = () => {
        const index = this.props.todoList.items.indexOf(this.props.listItem);
        this.props.todoList=[...this.props.todoList.filter(this.getItems(index))];
        this.props.loadList(this.props.todoList);
    }
    render() {
        return (
            <div className="list_item_delete_button"
            onClick={this.deleteItem}>

            </div>
        )
    }
}

export default DeleteButton
