import React, { Component } from 'react'

export class MoveupButton extends Component {
    moveUp = () => {
        console.log("HELLLO");
        const index = this.props.todoList.items.indexOf(this.props.listItem);
        if (index > 0) {
            const temp = this.props.listItem;
            this.props.todoList.items[index] = this.props.todoList.items[index-1];
            this.props.todoList.items[index-1] = temp;
        }
        //const index = this.state.currentList.indexOf()
        // const index = this.state.todoLists.indexOf(this.state.currentList);
        // if (index > 0) {
        //   const temp = this.state.todoLists[index];
        //   this.state.todoLists[index] = this.state.todoLists[index-1];
        //   this.state.todoLists[index-1] = temp;
        // }
    }
    render() {
        if (this.props.todoList.items.indexOf(this.props.listItem) === 0) {
            return (
                <div className="list_item_move_up_button disabled">

                </div>
            )
        }
        else {
            return (
            <div className="list_item_move_up_button"
            onClick={this.props.moveUp(this.moveUp)}>

            </div>
            )
        }
    }
}

export default MoveupButton
