import React, { Component } from 'react'
import ListHeading from './ListHeading'
import ListItemsTable from './ListItemsTable'
import ListTrash from './ListTrash'
import PropTypes from 'prop-types';

export class ListScreen extends Component {
    getListName() {
        if (this.props.todoList) {
            let name = this.props.todoList.name;
            return this.props.todoList.name;
        }
        else
            return "";
    }
    getListOwner() {
        if (this.props.todoList) {
            let owner = this.props.todoList.owner;
            return this.props.todoList.owner;
        }
    }

    changeName = e => {
        this.props.todoList.name = e.target.value;
        this.props.loadList(this.props.todoList);
    }

    changeOwner = e => {
        this.props.todoList.owner = e.target.value;
        this.props.loadList(this.props.todoList);
    }
    moveUp = () => {
        const index = this.state.todoLists.indexOf(this.state.currentList);
        if (index > 0) {
          const temp = this.state.todoLists[index];
          this.state.todoLists[index] = this.state.todoLists[index-1];
          this.state.todoLists[index-1] = temp;
        }
    }
    render() {
        return (
            <div id="todo_list">
                <ListHeading goHome={this.props.goHome} />
                <ListTrash 
                    deleteList={this.props.deleteList}/>
                <div id="list_details_container">
                    <div id="list_details_name_container" className="text_toolbar">
                        <span id="list_name_prompt">Name:</span>
                        <input 
                            onChange={this.changeName}
                            value={this.getListName()} 
                            type="text" 
                            id="list_name_textfield" />
                    </div>
                    <div id="list_details_owner_container" className="text_toolbar">
                        <span id="list_owner_prompt">Owner:</span>
                        <input 
                            onChange={this.changeOwner}
                            value={this.getListOwner()}
                            type="text" 
                            id="list_owner_textfield" />
                    </div>
                </div>
                <ListItemsTable todoList={this.props.todoList} moveUp={this.moveUp}/>
            </div>
        )
    }
}

export default ListScreen
